import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { Form, Button, Row, Col, InputGroup } from 'react-bootstrap';  // Added InputGroup
import { useDispatch, useSelector } from 'react-redux';
import Loader from '../components/Loader';
import Message from '../components/Message';
import FormContainer from '../components/FormContainer';
import { register } from '../actions/userActions';

// Import the password validator
import { validatePassword } from '../validators/passwordValidator';

// Import font-awesome for the eye icon (or you can use another icon library)
import { FaEye, FaEyeSlash } from 'react-icons/fa';  // Import eye icons

function RegisterScreen() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const [errors, setErrors] = useState({});
    
    // States for toggling password visibility
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);

    const dispatch = useDispatch();
    const location = useLocation();
    const navigate = useNavigate();

    const redirect = location.search ? location.search.split('=')[1] : '/';

    const userRegister = useSelector((state) => state.userRegister);
    const { error, loading, userInfo } = userRegister;

    useEffect(() => {
        if (userInfo) {
            navigate(redirect);
        }
    }, [navigate, userInfo, redirect]);

    const submitHandler = (e) => {
        e.preventDefault();

        // Validate password
        const validationErrors = validatePassword(password, confirmPassword);
        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
        } else {
            setErrors({});
            dispatch(register(name, email, password));
        }
    };

    return (
        <FormContainer>
            <h1>Register</h1>
            {message && <Message variant='danger'>{message}</Message>}
            {error && <Message variant='danger'>{error}</Message>}
            {loading && <Loader />}

            <Form onSubmit={submitHandler}>
                
                <Form.Group controlId='name'>
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                        required
                        type='text'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    ></Form.Control>
                </Form.Group>

                <Form.Group controlId='email'>
                    <Form.Label className='mt-3'>Email Address</Form.Label>
                    <Form.Control
                        required
                        type='email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    ></Form.Control>
                </Form.Group>

                {/* Password Field with Eye Icon */}
                <Form.Group controlId='password'>
                    <Form.Label className='mt-3'>Password</Form.Label>
                    <InputGroup>
                        <Form.Control
                            required
                            type={showPassword ? 'text' : 'password'}  // Toggle between password and text
                            value={password}
                            isInvalid={!!errors.password}  // Indicate validation error
                            onChange={(e) => setPassword(e.target.value)}
                        ></Form.Control>
                        <InputGroup.Text
                            onClick={() => setShowPassword(!showPassword)}  // Toggle showPassword state
                            style={{ cursor: 'pointer' }}
                        >
                            {showPassword ? <FaEyeSlash /> : <FaEye />}  {/* Toggle icon */}
                        </InputGroup.Text>
                        <Form.Control.Feedback type='invalid'>
                            {errors.password}
                        </Form.Control.Feedback>
                    </InputGroup>
                </Form.Group>

                {/* Confirm Password Field with Eye Icon */}
                <Form.Group controlId='passwordConfirm'>
                    <Form.Label className='mt-3'>Confirm Password</Form.Label>
                    <InputGroup>
                        <Form.Control
                            required
                            type={showConfirmPassword ? 'text' : 'password'}  // Toggle between password and text
                            value={confirmPassword}
                            isInvalid={!!errors.confirmPassword}  // Indicate validation error
                            onChange={(e) => setConfirmPassword(e.target.value)}
                        ></Form.Control>
                        <InputGroup.Text
                            onClick={() => setShowConfirmPassword(!showConfirmPassword)}  // Toggle showConfirmPassword state
                            style={{ cursor: 'pointer' }}
                        >
                            {showConfirmPassword ? <FaEyeSlash /> : <FaEye />}  {/* Toggle icon */}
                        </InputGroup.Text>
                        <Form.Control.Feedback type='invalid'>
                            {errors.confirmPassword}
                        </Form.Control.Feedback>
                    </InputGroup>
                </Form.Group>

                <Button type='submit' variant='primary' className='mt-3'>
                    Register
                </Button>
            </Form>

            <Row className='py-3'>
                <Col>
                    Have an Account?{' '}
                    <Link to={redirect ? `/login?redirect=${redirect}` : '/login'}>
                        Sign In
                    </Link>
                </Col>
            </Row>
        </FormContainer>
    );
}

export default RegisterScreen;
