import {
    ORDER_CREATE_REQUEST,
    ORDER_CREATE_SUCCESS,
    ORDER_CREATE_FAIL,

    ORDER_CREATE_RESET,

    ORDER_DETAILS_REQUEST,
    ORDER_DETAILS_SUCCESS,
    ORDER_DETAILS_FAIL,

    ORDER_PAY_REQUEST,
    ORDER_PAY_SUCCESS,
    ORDER_PAY_FAIL,
    ORDER_PAY_RESET,

    ORDER_LIST_MY_REQUEST,
    ORDER_LIST_MY_SUCCESS,
    ORDER_LIST_MY_FAIL,
    ORDER_LIST_MY_RESET,

    ORDER_LIST_REQUEST,
    ORDER_LIST_SUCCESS,
    ORDER_LIST_FAIL,

    ORDER_DELIVER_REQUEST,
    ORDER_DELIVER_SUCCESS,
    ORDER_DELIVER_FAIL,
    ORDER_DELIVER_RESET,
} from '../constants/orderConstants'


export const orderCreateReducer = (state = {}, action) => {
    switch (action.type) {
        case ORDER_CREATE_REQUEST:
            return {
                loading: true
            }

        case ORDER_CREATE_SUCCESS:
            return {
                loading: false,
                success: true,
                order: action.payload
            }

        case ORDER_CREATE_FAIL:
            return {
                loading: false,
                error: action.payload
            }

        case ORDER_CREATE_RESET:
            return {}


        default:
            return state
    }
}


export const orderDetailsReducer = (state = { loading: true, orderItems: [], shippingAddress: {} }, action) => {
    switch (action.type) {
        case ORDER_DETAILS_REQUEST:
            return {
                ...state,
                loading: true
            };

        case ORDER_DETAILS_SUCCESS:
            return {
                loading: false,
                order: {
                    ...action.payload // Ensure immutability here
                }
            };

        case ORDER_DETAILS_FAIL:
            return {
                loading: false,
                error: action.payload
            };

        default:
            return state;
    }
};


  


export const orderPayReducer = (state = {}, action) => {
    switch (action.type) {
        case ORDER_PAY_REQUEST:
            return {
                loading: true
            }

        case ORDER_PAY_SUCCESS:
            return {
                loading: false,
                success: true
            }

        case ORDER_PAY_FAIL:
            return {
                loading: false,
                error: action.payload
            }

        case ORDER_PAY_RESET:
            return {}

        default:
            return state
    }
};


export const orderDeliverReducer = (state = {}, action) => {
    switch (action.type) {
        case ORDER_DELIVER_REQUEST:
            return {
                ...state,  // Preserve existing state
                loading: true,
            };

        case ORDER_DELIVER_SUCCESS:
            return {
                ...state,  // Preserve existing state
                loading: false,
                success: true,
            };

        case ORDER_DELIVER_FAIL:
            return {
                ...state,  // Preserve existing state
                loading: false,
                error: action.payload,
            };

        case ORDER_DELIVER_RESET:
            return {
                ...state,  // Preserve any other state you want to keep
                success: false, // Reset the success status if needed
                error: null,    // Reset the error
            };

        default:
            return state;
    }
};



export const orderListMyReducer = (state = { orders: [] }, action) => {
    switch (action.type) {
        case ORDER_LIST_MY_REQUEST:
            return {
                ...state,  // Keep the current state properties
                loading: true
            };

        case ORDER_LIST_MY_SUCCESS:
            return {
                ...state,  // Keep the current state properties
                loading: false,
                orders: action.payload
            };

        case ORDER_LIST_MY_FAIL:
            return {
                ...state,  // Keep the current state properties
                loading: false,
                error: action.payload
            };

        case ORDER_LIST_MY_RESET:
            return {
                orders: []  // Reset orders while other state properties remain intact
            };

        default:
            return state;
    }
};



export const orderListReducer = (state = { orders: [] }, action) => {
    switch (action.type) {
        case ORDER_LIST_REQUEST:
            return {
                ...state, // Preserve existing state
                loading: true,
                error: null // Clear any previous error
            };

        case ORDER_LIST_SUCCESS:
            return {
                ...state, // Preserve existing state
                loading: false,
                orders: action.payload,
                error: null // Clear any previous error
            };

        case ORDER_LIST_FAIL:
            return {
                ...state, // Preserve existing state
                loading: false,
                error: action.payload
            };

        default:
            return state;
    }
};
